import React, { Component } from 'react';
import Helmet from 'react-helmet';
import { Link } from 'react-router-dom';

import './IndexWallets.css';

export default class IndexWallets extends Component {
  displayWallets(e){
    var element = e.currentTarget;
    if(e.target.id == 'desktop-button'){
      var desktop = document.getElementsByClassName('desktop')[0];
      console.log('made it!')
      desktop.className += " active";
      var children = desktop.children;
      for (var i = 0; i < children.length; i++) {
        children[i].style.display = "flex";
      }

    }
  }

  displayDownloadLink(e){
    if (e.target.id == 'windows_button') {
      var windows = document.getElementById('windows');
      windows.style.display = 'flex';

    }

  }


  render() {
    return (
      <div className = "wallets">
          <div className = "wallets__title">
              <h2 className = "todo__title__text" >Wallets</h2>
          </div>
          <div className = "wallets__content">
              <p className = "wallets__content__text">
              Choose your wallet!
              </p>
              <div className = "wallets__buttons">
                <div id = "desktop-button" className = "button" onClick = {this.displayWallets}>Desktop Wallet</div>
                <div id = "mobile-button" className = "button"  onClick = {this.displayWallets}>Mobile Wallet</div>
                <div id = "online-button" className = "button"  onClick = {this.displayWallets}>Online Wallet</div>
              </div>
              <div className = "desktop">
                <div id = "windows_button" className = "desktop__button" onClick = {this.displayDownloadLink}>Microsoft</div>
                <div id = "mac_button" className = "desktop__button" onClick = {this.displayDownloadLink}>MacOS</div>
                <div id = "linux_button"className = "desktop__button" onClick = {this.displayDownloadLink}>Linux</div>
              </div>
              <a id = "windows" className = "dwnLink" href = "https://github.com/tutor-web/smileyCoin/releases/download/v2.2.2/smileycoin-qt.exe">Download the windows wallet!</a>
              <a id = "mac" className = "dwnLink" href = "https://github.com/tutor-web/smileyCoin/releases/download/v2.2.2/Smileycoin-Qt.dmg">Download the Mac wallet!</a>
              <a id = "linux" className = "dwnLink" href = "https://github.com/tutor-web/smileyCoin/releases/download/v2.2.2/smileycoin-qt.exe">Download the linux wallet!</a>
          </div>
      </div>
    );
  }
}
