import React, { Component } from 'react';
import Helmet from 'react-helmet';
import { Link } from 'react-router-dom';
import { Timeline } from 'react-twitter-widgets'
import { Nav, Card, Button } from 'react-bootstrap'

import './Wallets.css';

export default class Wallets extends Component {
  hideWallets(){
    var desktop = document.getElementsByClassName('desktop')[0];
    var mobile = document.getElementsByClassName('mobile')[0];
    var online = document.getElementById('online');
    online.style.display = "none";
    for (var i = 0; i < desktop.children.length; i++) {
      desktop.children[i].style.display = "none"
    }
    for (var i = 0; i < mobile.children.length; i++) {
      mobile.children[i].style.display = "none";
    }
  }

  displayWallets(e){
      var windows = document.getElementById('windows');
      var mac = document.getElementById('mac');
      var linux = document.getElementById('linux');

      windows.classList.remove("slide");
      mac.classList.remove("slide");
      linux.classList.remove("slide");


      var desktop = document.getElementsByClassName('desktop')[0];
      var mobile = document.getElementsByClassName('mobile')[0];
      var online = document.getElementById('online');
      desktop.classList.remove("active")
      mobile.classList.remove("active")
      online.classList.remove("active")

      online.style.display = "none";
      for (var i = 0; i < desktop.children.length; i++) {
        desktop.children[i].style.display = "none"
      }
      for (var i = 0; i < mobile.children.length; i++) {
        mobile.children[i].style.display = "none";
      }
      var windows = document.getElementById('windows');
      var mac = document.getElementById('mac');
      var linux = document.getElementById('linux');
      /*
      windows.style.display = 'none';
      mac.style.display = 'none';
      linux.style.display = 'none';
      */
      var android = document.getElementById('android');
      var ios = document.getElementById('ios');
      android.style.display = 'none';
      ios.style.display = 'none';
    if(e.target.id == 'desktop-button'){
      var desktop = document.getElementsByClassName('desktop')[0];
      console.log('made it!')
      desktop.classList.toggle("active");
      var children = desktop.children;
      for (var i = 0; i < children.length; i++) {
        children[i].style.display = "flex";
      }
    } else if(e.target.id == 'mobile-button'){
      var mobile = document.getElementsByClassName('mobile')[0];
      mobile.classList.toggle("active");
      mobile.style.display = "flex";
      var children = mobile.children;
      var android  = document.getElementById('')
      for (var i = 0; i < children.length; i++) {
        children[i].style.display = "flex";
      }
    } else if(e.target.id == 'online-button'){
      var online = document.getElementById('online');
      online.style.display = "flex";
    }
  }

  displayDownloadLink(e){
    var windows = document.getElementById('windows');
    var mac = document.getElementById('mac');
    var linux = document.getElementById('linux');
    /*
    windows.style.display = 'none';
    mac.style.display = 'none';
    linux.style.display = 'none';
    */
    windows.classList.remove("slide");
    mac.classList.remove("slide");
    linux.classList.remove("slide");

    if (e.target.id == 'windows_button') {
      windows.style.display = 'flex';
      windows.classList.toggle("slide");
    } else if (e.target.id == 'mac_button'){
      mac.style.display = 'flex';
      mac.classList.toggle("slide");
    } else if (e.target.id == 'linux_button'){
      linux.style.display = 'flex';
      linux.classList.toggle("slide");
    }
  }
  displayMobileLinks(e){
    var android = document.getElementById('android');
    var ios = document.getElementById('ios');
    android.style.display = 'none';
    ios.style.display = 'none';
    if(e.target.id == 'android_button'){
      android.style.display = 'flex';
    } else if (e.target.id == 'ios_button'){
      ios.style.display = 'flex';
    }
  }
  render() {
    return (
      <div className = "wallets">
          <div className = "wallets__title">
              <h2 className = "todo__title__text" >Wallets</h2>
          </div>
          <div className = "wallets__content">
              <p className = "wallets__content__text">
              Choose your wallet!
              </p>
              <div className = "buttons">
                <div className = "wallets__buttons">
                  <div id = "desktop-button" className = "button" onClick = {this.displayWallets}>Desktop Wallet</div>
                  <div id = "mobile-button" className = "button"  onClick = {this.displayWallets}>Mobile Wallet</div>
                  <div id = "online-button" className = "button"  onClick = {this.displayWallets}>Online Wallet</div>
                </div>
                <div className = "desktop">
                  <div id = "windows_button" className = "desktop__button button" onClick = {this.displayDownloadLink}>Microsoft</div>
                  <div id = "mac_button" className = "desktop__button button" onClick = {this.displayDownloadLink}>MacOS</div>
                  <div id = "linux_button"className = "desktop__button button" onClick = {this.displayDownloadLink}>Linux</div>
                </div>
                <div className = "mobile">
                  <div id = "android_button" className = "mobile__button button" onClick = {this.displayMobileLinks}>Android</div>
                  <div id = "ios_button" className = "mobile__button button" onClick = {this.displayMobileLinks}>iOS</div>
                </div>
                <div className = "dl-cards">
                <div class="dl-card" id = "windows">
                  <img class="dl-card-img-top" src="..." alt="Card image cap"></img>
                  <div class="dl-card-body">
                    <h5 class="dl-card-title">Windows</h5>
                    <p class="dl-card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                    <a className = "dwnLink" href = "https://play.google.com/store/apps/details?id=com.coinomi.wallet&hl=en"><img class = "dl-card-link" src = {require('../../img/google-play-badge.png')}></img></a>
                  </div>
                </div>
                <div class="dl-card" id = "mac">
                  <img class="dl-card-img-top" src="..." alt="Card image cap"></img>
                  <div class="dl-card-body">
